package loops;
//actually nessted loop
public class ForLoops2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int counter=0;
		int i;
		for(i=1; i<=5; i++)
		{
	
		for(counter=1; counter<=10; counter=counter+1)
		{
			System.out.println("Hello " +i); //swap out variable
	
		}
		}
		
	}

}
